import React, { useState, ChangeEvent } from "react";


interface FormProps{
    onAdd(title?:string,body?:string):void
}
// interface Itext {
//     title: string;
//     body: string;
//   }
const Form: React.FC<FormProps> = (props) => {
    const initialState = {
        title: null,
        body: null,
      };
  const [textTitle, setTextTitle] = useState<string>();
  const [textBody, setTextBody] = useState<string>();
  const [textState, setTextState] = useState(initialState);


//   const changeHandler=(event:ChangeEvent<HTMLInputElement>)=>{
//     setTextState({title:event[0].target.value,body:event[1].target.value})
// }













  const changeHandlerTitle=(event:ChangeEvent<HTMLInputElement>)=>{
    //   setTextTitle(event.target.value)
    setTextState()
  }
  const changeHandlerBody=(event:ChangeEvent<HTMLInputElement>)=>{
    //   setTextBody(event.target.value)
    setTextState({title:event[0].target.value,body:event[1].target.value})
  }
  const keyPress=(event:React.KeyboardEvent)=>
  {
// if(event.key==='Enter'){
//    props.onAdd(textTitle!,textBody!)
  
//     setTextTitle('')
//     setTextBody('')

// }
if(event.key==='Enter'){
   props.onAdd(textState.title,textState.body)
   console.log('textState', textState)
   setTextState({title:"",body:""})
  
//  setTextState('')

}
  }
  return (
    // <div className="input-field">
    //     <form className='form'>
    //   <input 
    //   onChange={changeHandlerTitle}
    //   value={textTitle}
    //   onKeyPress={keyPress}
    //   placeholder='Enter Title'
    //   type="text" id="title" />
    //   <label htmlFor="title" className="active">
    //   Enter Title
    //   </label>
    //   <input 
    //   onChange={changeHandlerBody}
    //   value={textBody}
    //   onKeyPress={keyPress}
    //   placeholder='Enter description'
    //   type="text" id="body" />
    //   <label htmlFor="body" className="active">
    //  Enter description
    //   </label>
    //   <h3> Fill the blanks and press Enter</h3>
    //   </form>
    // </div>
    <div className="input-field">
        <form className='form'>
      <input 
      onChange={changeHandlerTitle}
      value={textState.title}
      onKeyPress={keyPress}
      placeholder='Enter Title'
      type="text" id="title" />
      <label htmlFor="title" className="active">
      Enter Title
      </label>
      <input 
      onChange={changeHandlerBody}
      value={textState.body}
      onKeyPress={keyPress}
      placeholder='Enter description'
      type="text" id="body" />
      <label htmlFor="body" className="active">
     Enter description
      </label>
      <h3> Fill the blanks and press Enter</h3>
      </form>
    </div>
  );
};
export default Form
