import { useState } from "react";
import axios from "axios";
import Link from "next/link";
import Form from "../../components/form/Form";
axios.defaults.headers.post["Content-Type"] = "application/json; charset=UTF-8";

interface Ititle {
  title: string;
}
interface IBody {
  body: string;
}

const createPost = () => {
  const [postTitle, setPostTitle] = useState<Ititle>(null);
  const [postBody, setPostBody] = useState<IBody>(null);

  const pushPost = async () => {
    try {
      const data = await axios({
        method: "post",
        url: "https://simple-blog-api.crew.red/posts/",
        data: { ...postTitle, ...postBody },
      });
    } catch (error) {
      console.log("POST ERROR", error);
      throw new Error();
    }
  };
  if (postTitle && postBody) {
    pushPost();
  }

  const addhandler = (title: string, body: string) => {
    const titlePost: Ititle = {
      title: title,
    };
    const postMain: IBody = {
      body: body,
    };

    setPostTitle(titlePost);
    setPostBody(postMain);
  };

  return (
    <>
      <Form onAdd={addhandler} />
      <div className="backToHome">
        <Link href="/">
          <a>‚Üê Back to home</a>
        </Link>
      </div>
    </>
  );
};
export default createPost;
