import { useState } from "react";
import axios from "axios";
import Link from "next/link";
import Form from "../../components/form/Form";
axios.defaults.headers.post["Content-Type"] = "application/json; charset=UTF-8";

interface IForm {
  title: string;
  // body:string
}

// const postToAdd = {
//     title: 'NEXT JS',
//     body: 'Typescript',
//   };
const createPost = () => {
  const [postText, setPostText] = useState<IForm>();

  const pushPost = async () => {
    try {
      const data = await axios({
        method: "delete",
        url: "https://simple-blog-api.crew.red/posts/27",
        // data: postText,
      });
    } catch (error) {
      console.log("POST ERROR", error);
      throw new Error();
    }
  };
  
    // pushPost();
  
    // pushPost();
  
  const addhandler = (title: string) => {
    const post: IForm = {
      title: title,
      // body:body
    };

    setPostText((post));
    console.log("textState", postText);
  };

  return (
    <>
      {/* <Form onAdd={addhandler} /> */}

      <div className="backToHome">
        <Link href="/">
          <a>‚Üê Back to home</a>
        </Link>
      </div>
    </>
  );
};
export default createPost;
